import { useEffect } from "react";
import { createContext, useState } from "react";

export const CartContext = createContext({
  cartItems: [],

  getItemQuantity: () => {},
  addOneToCart: () => {},
  removeOneToCart: () => {},
  deleteItemToCart: () => {},

  isCartOpen: false,
  setIsCartOpen: () => {},
  cartCounter: 0,
  totalCounter: 0,
});

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);

  const [isCartOpen, setIsCartOpen] = useState(false);
  const [cartCounter, setCartCounter] = useState(0);
  const [totalCounter, setTotalCounter] = useState(0);

  const getItemQuantity = (id) => {
    const quantity = cartItems.find((item) => item.id === id)?.quantity;

    if (quantity === undefined) {
      return 0;
    }

    return quantity;
  };

  const addOneToCart = (id) => {
    const quantity = getItemQuantity(id);

    if (quantity === false) {
      return cartItems.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      );
    }

    return [...cartItems, { id: id, quantity: 1 }];
    // if (quantity === 0) {
    //   setCartItems([
    //     ...cartItems,
    //     {
    //       id: id,
    //       quantity: 1,
    //     },
    //   ]);
    // } else {
    //   setCartItems(
    //     cartItems.map((item) =>
    //       item.id === id ? { ...item, quantity: item.quantity + 1 } : item
    //     )
    //   );
    // }
  };

  const removeOneToCart = (id) => {
    const quantity = getItemQuantity(id);

    if (quantity === 1) {
      deleteItemToCart(id);
    } else {
      setCartItems(
        cartItems.map((item) =>
          item.id === id ? { ...item, quantity: item.quantity - 1 } : item
        )
      );
    }
  };

  const deleteItemToCart = (id) => {
    setCartItems(
      cartItems.filter((item) => {
        return item.id !== id;
      })
    );
  };

  useEffect(() => {
    const newCartCount = cartItems.reduce(
      (total, cartItem) => total + cartItem.quantity,
      0
    );
    setCartCounter(newCartCount);
  }, [cartItems]);

  useEffect(() => {
    const newTotalCounter = cartItems.reduce(
      (total, cartItem) => total + cartItem.price * cartItem.quantity,
      0
    );
    setTotalCounter(newTotalCounter);
  }, [cartItems]);

  const value = {
    cartItems,

    getItemQuantity,
    addOneToCart,
    removeOneToCart,
    deleteItemToCart,

    isCartOpen,
    setIsCartOpen,
    cartCounter,
    totalCounter,
  };

  return <CartContext.Provider value={value}>{children}</CartContext.Provider>;
};
