import './checkout-item.styles.scss'
import { useContext } from 'react'
import { CartContext } from '../../contexts/cart.context'


export const CheckOutItem = ({cartItem}) => {
    const {id, name , imageUrl, price, quantity} = cartItem
    const { addItemToCart, decreaseQuantity } = useContext(CartContext)

    const increase = () => {
        addItemToCart(cartItem)
      }

      const decrease = () => {
        decreaseQuantity(cartItem)
      }
  return (
    <div className="checkout-item-container" key={id}>
        <div className="image-container">
            <img src={imageUrl} alt={name} />
        </div>
        <div className="name">
            {name}
        </div>
        <div className="quantity">
            <div className="arrow" onClick={decrease}>&lt;</div>
            <div className="value">{quantity}</div>
            <div className="arrow" onClick={increase}>&gt;</div>

        </div>
        <div className="price">
            ${price * quantity}
        </div>
        <div className='remove-button'>X</div>
    </div>
  )
}
